<script setup>
import { ref } from 'vue';
import {Logout} from '@/api/user'
import ResponseStatusEnum from '@/enums/ResponseStatusEnum'
import { useRouter } from 'vue-router';
import {showMessage} from '@/utils/showMessage'
import MessageTypeEnum from '@/enums/messageTypeEnum'
import {userStoreInstance} from '@/stores/'
const router = useRouter();
const menuTrigger = ref('hover')
const switchValue = ref(true)
const isCollapse = ref(false)
const activeIndex = ref(1);
const changeDirection = (event) => {
    isCollapse.value = !isCollapse.value
}

const logout = (event) => {
    ElMessageBox.confirm(
        '确定要退出登录吗？',
        'Warning',
        {
            confirmButtonText: '确定',
            cancelButtonText: '取消',
            type: 'warning',
    }
    ).then(async () => {
        await Logout(userStoreInstance.getUser()?.id).then(res => {
        if (res.code === ResponseStatusEnum.SUCCESS) {
            router.push('/login')
            showMessage("退出登录", MessageTypeEnum.SUCCESS)
        }
    }).catch(error => {}) 
    }).catch(error=> {

    })
}
</script>

